var filterTitles=document.querySelectorAll(".filter-title");filterTitles.forEach((filterTitle)=>{filterTitle.addEventListener("click",function(){console.log("click me");var filterContent=this.nextElementSibling;filterTitle.classList.toggle("collapsed");filterContent.classList.toggle("active");});});window.addEventListener("popstate",function(e){var pageNum=getPageNumberFromURL();updatePaginationActive(pageNum);});function getPageNumberFromURL(){var url=window.location.href;var params=new URLSearchParams(url);return params.get("page");}
function updatePaginationActive(pageNum){var links=document.querySelectorAll(".pagination a");for(var i=0;i<links.length;i++){if(links[i].textContent==pageNum){links[i].classList.add("active");}else{links[i].classList.remove("active");}}};